let Buttons=document.querySelectorAll(".ModalTrigger");const Scenarios=[{Name:"Help",Title:"Få hjælp til login",Content:"Ring til telefon nr. <br> +45 12 13 14 15"},{Name:"Info",Title:"Den gør noget",Content:"Pop"}];function AddListeners(e){e.addEventListener("click",(function(e){var t=e.target.getAttribute("id"),n=document.querySelector("#Modal");let o=Scenarios.findIndex((e=>e.Name===t));n.querySelector("#ModalTitle").innerHTML=Scenarios[o].Title,n.querySelector("#ModalContent").innerHTML=Scenarios[o].Content,n.classList.add("Display")}))}function closeHelp(){document.querySelector("#Modal").classList.remove("Display")}Buttons.forEach(AddListeners);